// grab our gulp packages
var
    gulp  = require ('gulp'),
    watch = require ('gulp-watch'),
    clean = require ('gulp-clean'),
    concat = require ('gulp-concat'),
    debug = require ('gulp-debug'),
    rename = require ('gulp-rename'),
    vinylPaths = require ('vinyl-paths'),
    uglify = require ('gulp-uglify')

function compile (target) {
    return gulp.src(['./vendor/uuid-v4.js', './src/Tamed.js', './src/TamedSimple.js'])
        .pipe (debug ({title: 'compile:'}))
        .pipe (concat (target))
        .pipe (gulp.dest ('./dist/'))
}

gulp.task ('live-build', function () {
    return watch ('src/**/*.js', gulp.parallel ('build'));
})

gulp.task ('clean', function () {
    return gulp.src (['./dist/*'], {read: false})
        .pipe (debug ({title: 'clean:'}))
        .pipe (clean ());
})

gulp.task ('compile-tamed', function () {
    return compile ('tamed.js')
})

gulp.task ('compile-tamed-simple', function () {
    return compile ('tamed-simple.js')
})

gulp.task ('compile-tamed-full', function () {
    return compile ('tamed-full.js')
})

gulp.task ('minify', function () {
    return gulp.src (['./dist/*.js'])
        .pipe (debug ({title: 'minify:'}))
        .pipe (uglify ())
        .pipe (rename ({suffix: '.min'}))
        .pipe (gulp.dest ('./dist/'))
})

// create a default task and just log a message
gulp.task ('build', gulp.series ('clean', 'compile-tamed', 'compile-tamed-simple', 'compile-tamed-full', 'minify'));
gulp.task ('default', gulp.series ('clean'));
