tm.add ('test.simple.Object1', function (o, p, d) {
    // Unique methods.
    function modifyAge (years) {
        return d.age + years;
    }

    // Shared methods.
    o.setup = function (config) {
        // Set internal values from config.
        d.name = config.name;
        d.age = parseInt (config.age);
    }

    o.getName = function () {
        return d.name;
    }

    o.getAge = function (addYears) {
        if (addYears) {
            return modifyAge (addYears);
        }

        return d.age;
    }
});
