tm.add ('test.simple.OverrideMethods', function (o, p, d) {
    // Set internal values from config.
    d.name = d._config.name;
    d.age = parseInt (d._config.age);

    // Unique methods.
    function modifyAge (years) {
        return d.age + years;
    }

    // Shared methods.
    // o._override = {};
    // o._override.getName = function () {
    // }

    // o.override = {
    //     getName: function () {
    //     },
    //     getAge: function () {
    //     }
    // }

    // Return a shared object.
    return {
        getName: function () {
            return d.name;
        },
        override: {
            getAge: function (addYears) {
                if (addYears) {
                    return modifyAge (addYears);
                }

                return d.age;
            },
        }
    };
});
