describe ('Tamed Simple Handlebars', function() {
    it ('should compile and add a Handlebars template file.', function (done) {
        tm.load ({
            list: [
                '/src/test/sample/Sample1.hbs',
            ],
            done: function (data) {
                var dom, selector;

                selector = '#render-area';
                tm.html ('trg.handlebars.test.Sample1', {
                    selector: selector,
                    data: {
                        id: 'test-1',
                        name: 'Test',
                        details: 'Here are additional details...',
                    }
                })

                expect ($('#test-1 h3').html ()).to.equal ('Sample 1: Test');
                expect ($('#test-1 p').html ()).to.equal ('This is a sample Handlebars file\'s content. Here are additional details...');
                done ();
            }
        });
    })
    it ('should append compiled template to existing dom', function (done) {
        tm.load ({
            list: [
                '/src/test/sample/Sample1.hbs',
            ],
            done: function (data) {
                var dom, selector;

                selector = '#render-area';
                tm.html ('trg.handlebars.test.Sample1', {
                    selector: selector,
                    data: {
                        id: 'test-2',
                        name: 'Test',
                        details: 'Here are additional details...',
                    },
                    append: true
                })

                expect ($('#test-1 h3').html ()).to.equal ('Sample 1: Test');
                expect ($('#test-1 p').html ()).to.equal ('This is a sample Handlebars file\'s content. Here are additional details...');

                expect ($('#test-2 h3').html ()).to.equal ('Sample 1: Test');
                expect ($('#test-2 p').html ()).to.equal ('This is a sample Handlebars file\'s content. Here are additional details...');
                done ();
            }
        });
    })
});
