'use strict';
tm.add ('trg.loader.simple.Loader', ['trg.loader.simple.Cache'], function (o, p, d) {
    o.setup = function () {
        d.loadList = [];
    }

    o.load = function (data) {
        var queue;

        queue = tm.new ('trg.loader.simple.LoaderQueue');
        queue.load (data);
        d.loadList.push (queue);
    }
})
