'use strict';
// Set the loader to load first.
tm.start (function () {
    tm.load ({
        setLoader: true,
        list: [],
        done: function () {
            console.log ('- Using the Tamed Simple Handlerbars html Templater.')
            tm.setHtmlTemplater (tm.new ('trg.handlebars.simple.Templater'))
        }
    });    
})
